import React, { useState } from 'react';

import Card from '../card/Card';
import ResultsList from '../resultlist/ResultsList';
import SearchBar from '../searchbar/SearchBar';

import './pokemonSearch.css';

function PokemonSearch() {

  const pokedata = [
    { id: 1, name: "Charmander", Type: "Fire" },
    { id: 2, name: "Pikachu", Type: "Electricity" },
    { id: 3, name: "Togepi", Type: "Normal" },
    { id: 4, name: "Jigglypuff", Type: "Normal" }
  ]

  const [pokemonList, setPokemonList] = useState(pokedata)
  const [nameInput, setNameInput] = useState("")
  const [typeInput, setTypeInput] = useState("")

  const handleNameInputChange = (event) => {
    setNameInput(event.target.value)
  }

  function filteredDataByName(dataArray, nameInput) {
    return (
      dataArray.filter((i) =>
        i.name.toLowerCase() == nameInput
      )
    )
  }

  return (
    <div className="container">

      <div className="card-container">
        <Card />
      </div>

      <div className="search-container">
        <SearchBar onNameInputChange={handleNameInputChange} />
        <h1>{nameInput}</h1>
        <ResultsList results={pokemonList} />
      </div>

    </div>
  );
}

export default PokemonSearch;
